    case MsgName of
      'Dependency' -> v_msg_Dependency(Msg, ['Dependency']);
      'Release' -> v_msg_Release(Msg, ['Release']);
      'Package' -> v_msg_Package(Msg, ['Package']);
      _ -> mk_type_error(not_a_known_message, Msg, [])
    end.


